import React from "react"
import {MemberConsumer} from"./Context";
import {NavLink} from "react-router-dom";
import {ButtonContainer} from "./Button"
import Title from "./Title"
import styled from 'styled-components';

function Details () {
    return (
      <MemberConsumer>
      	{value  =>{
      const {id, name, img, about,profession, location, inNetwork}=
      value.MemberDetail;
      return(
      <div>
      <h2>User profile</h2>

        {/* "+" button adds user to personal network of mentees. If added then toggle to "-"		
 			*/}
        <h2>+</h2>
        <p>menteepower</p>
        <p>mentorpower</p>
        <p>available MNT</p>
        <p>staked MNT</p>
        <p>total MNT</p>


      	<div className="container py-5">

      	{/* name */}
      	<div className="row">
      	<div className="col-10 mx-auto text-center text-slanted text-black my-5">
      	<h1>{name}</h1>
      </div>
      </div>
      {/* end name */}
    	{/* member info */}

       <MyContainer>
       <div className="col-10 mx-auto col-md-6 my-3 p3">
       	<img src={img} className="img-fluid" style={{width: "30rem", height: "30rem"}} alt="member"/>
       	<div class="button my-3 p-3" ><NavLink to="/MyNetworkMentees">MY NETWORK</NavLink></div>
       	</div>
       </MyContainer>

       	 {/* about member text */}
       	 <div className="container-fluid my-3">
       	 	<h2>About me:</h2>
       	 	<h4 className="text-title text-uppercase text-muted mt-3 mb-2"></h4>
       		<p className="text-muted lead">{about}</p>
       			<div>
		<NavLink to="/Members">
			<ButtonContainer>back to members</ButtonContainer>
		</NavLink>
			<ButtonContainer MyNetworkMentees
				disabled={inNetwork? true:false}
					onClick={() => {
					value.addMember(id);
					value.openModal(id);
					}}
					>
					{inNetwork? "in Network" : "add to network"}
					</ButtonContainer>


      <h3>Account activity timeline</h3>


      	<NavLink to="/members">Connect with other members</NavLink>


      	<h3>Blockchain CV</h3>      
 		<div class="button"><NavLink to="/cvgeneral">GENERAL</NavLink></div>
 		<div class="button"><NavLink to="/cvcourses">COURSES</NavLink></div>



 		<ul>
 		<li>Experience</li>
 		<li>Skills</li>
 		<li>Education</li>
 		</ul>
 		</div>
 		</div>
       		</div>
       	 </div>
      );
      }}
      </MemberConsumer>
    )
  }


export default Details

const MyContainer = styled.div`

img{
	border-radius:50%;

}

.button{
	margin: 0 auto
}
`
