import React, {Component}  from "react"
import styled from "styled-components";

import {NavLink,} from "react-router-dom";
import PropTypes from 'prop-types';



export default class HomeCards extends Component {
render () {

    return (

    <CardWrapper className="col-10  mx-auto col-md-3 ">


        <div class="row">
  <div class="column">
    <div class="card"><i class="fas fa-users"></i><NavLink to="/members">Find mentors</NavLink>
    <p>Get an unlimited access to the community of people who want to share their knowledge and experience with you and help with disclosing your potential</p></div>
  </div>
  <div class="column">
    <div class="card"><i class="fas fa-tools"></i><NavLink to="/DesignCourse">Perform tasks</NavLink>
  <p>Perform tasks given you by mentors and see how your knowledge and experience increases and your Menteepower grows</p></div>
  </div>
  <div class="column">
    <div class="card"><i class="fas fa-search"></i><NavLink to="/members">Be discovered</NavLink>
  <p>Employers come here to shape the talent and incorporate the best ones into their own businesses. Let them find you and give you a chance</p></div>
  </div>
  <div class="column">
    <div class="card"><i class="far fa-address-card"></i><NavLink to="/details">Forge your CV</NavLink>
  <p>Each task you perform is a new skill recorded on the blockchain. Employers can find you easily by looking up specific skills.</p></div>
  </div>
</div>

    </CardWrapper>

    );
  }
}



const CardWrapper = styled.div`

.card{
	border-color:transparent;
	transition:all 0.5s linear;

	 box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2);
  padding: 16px;
  text-align: center;
  background-color: #f1f1f1;
}

.column {
  float: left;
  width: 25%;
  padding: 0 10px;
}

.card-footer{
	border-color:transparent;
	color: black;
	background-color:white;
	transition:all 0.5s linear;

}
&:hover{
	.card{
	border:0.06rem solid rgba(1,0,0,0.3);
	box-shadow:2px 2px 5px 0px rgba(1,0,0,0.2)
}
	.card-footer {
	background: rgba(250,250,250);
	}}

.images-container{
	position:relative;
	overflow:hidden;

	}	
.card-images-top{
	transition: all 1s linear;
}
.images-container:hover .card-img-top{
	transform:scale(1.2);

	}

.add-btn{
	position:absolute;
	bottom:0;
	right:0;

	background:var(--lightGreen);
	border:none;
	color: var(--mainWhite);
	transform:translate(100%,100%);
	transition:all 0.5s linear;
}
.images-container:hover .add-btn {
	transform: translate(0,0);

}
img{
	border-radius:50%
}


.add-btn:hover{
	background:var(--mainWhite);
	border:0.06rem solid;
	color: var(--lightGreen);
	cursor:pointer;}
`;
