import React, {Component}  from "react"
import styled from "styled-components";

import {NavLink,} from "react-router-dom";
import PropTypes from 'prop-types';



export default class HomeCards extends Component {
render () {

    return (

    <CardWrapper className="col-10  mx-auto">
    <div class="card-deck">
        <div class="row">
  			<div class="column">
    			<div class="card" >
    				<div class="images-container">
    					<i class="fas fa-users" class="card-img-top"></i>
    				</div>
    				<h5 class="card-title"><NavLink to="/members">FIND MENTORS</NavLink></h5>
    				<div class="card-body">
    					<p>Get an unlimited access to the community of people who want to share their knowledge and experience with you and help with disclosing your potential</p></div>
  					</div>
  				</div>
  			<div class="column">
    				<div class="card"><i class="fas fa-tools"></i><NavLink to="/DesignCourse">PERFORM TASKS</NavLink>
  <div class="card-body">
  <p>Perform tasks given you by mentors and see how your knowledge and experience increases and your Menteepower grows</p></div>
  </div>
  </div>
  <div class="column">
    <div class="card"><i class="fas fa-search "></i><NavLink to="/members">BE DISCOVERED</NavLink>
  <div class="card-body">
  <p>Employers come here to shape the talent and incorporate the best ones into their own businesses. Let them find you and give you a chance</p></div>
  </div>
  </div>
  <div class="column">
    <div class="card"><i class="far fa-address-card"></i><NavLink to="/details">FORGE YOUR CV</NavLink>
  <div class="card-body">
  <p>Each task you perform is a new kind of skill recorded on the blockchain. Employers can find you easily by looking up specific skills.</p></div>
  </div>
  </div>
</div>
</div>
    </CardWrapper>

    );
  }
}



const CardWrapper = styled.div`

.card{
width: 15rem;
color: black;
	border-color:transparent;
	transition:all 0.5s linear;

	 box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2);
  padding: 16px;
  text-align: center;
 background: rgba(20,224,129,1);
background: -moz-linear-gradient(top, rgba(20,224,129,1) 0%, rgba(20,224,129,1) 8%, rgba(12,245,187,1) 100%);
background: -webkit-gradient(left top, left bottom, color-stop(0%, rgba(20,224,129,1)), color-stop(8%, rgba(20,224,129,1)), color-stop(100%, rgba(12,245,187,1)));
background: -webkit-linear-gradient(top, rgba(20,224,129,1) 0%, rgba(20,224,129,1) 8%, rgba(12,245,187,1) 100%);
background: -o-linear-gradient(top, rgba(20,224,129,1) 0%, rgba(20,224,129,1) 8%, rgba(12,245,187,1) 100%);
background: -ms-linear-gradient(top, rgba(20,224,129,1) 0%, rgba(20,224,129,1) 8%, rgba(12,245,187,1) 100%);
background: linear-gradient(to bottom, rgba(20,224,129,1) 0%, rgba(20,224,129,1) 8%, rgba(12,245,187,1) 100%);
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#14e081', endColorstr='#0cf5bb', GradientType=0 );}

.column {
  float: left;
  width: 25%;
  padding: 0 10px;

}


&:hover{
	.card{
	border:0.06rem solid rgba(1,0,0,0.3);
	box-shadow:2px 2px 5px 0px rgba(1,0,0,0.2)
}
	.card-footer {
	background: rgba(250,250,250);
	}}

.images-container{
	position:relative;
	overflow:hidden;

	}	
.card-images-top{
	transition: all 1s linear;
}
.images-container:hover .card-img-top{
	transform:scale(1.2);

	}

.images-container:hover .add-btn {
	transform: translate(0,0);

}
img{
	width: 50;
	height:50;
	border-radius:50%;
}


`;
