import React, {Component}  from "react"
import styled from "styled-components";
import {MemberConsumer} from "./Context";
import {
NavLink,
}
 from "react-router-dom";

export default class MemberCard extends Component {
render () {

const {id, name, img, location, inNetwork} = this.props.member;
    return (
    <MemberWrapper
      	div className="col-9  col-md-5 col-lg-6 my-1">
        <div className = "card">
        <div className = "images-container p-3" onClick={()=> console.log("you clicked me")}>
        <NavLink to = "/Details">
        <img src = {img} alt = "member" className= "card-img-to"/>
      </NavLink>
      <button className="add-btn" disabled={inNetwork ? true : false} onClick={()=>{console.log('added to the network of members');}}>
      {inNetwork?(<p className="text-capitalize mb-0" disabled>
      {" "}
      in my Network
      </p>)
      :(<i className="fas fa-plus" />)}
      </button>
      </div>
      {/* card footer */}
      <div className="card-footerd-flex justify-content-between">
      <h3 className="align-self-center mb-10">
      {name}
      </h3>
      <p className=" font-italic mb-0">
      <span className="mr-1"></span>
      {location}
      </p>
      </div>
      </div>
      </MemberWrapper>
    );
  }
}
const MemberWrapper = styled.div`background-color: green;`
