import React, {Component}  from "react"
import {MembersData, MemberDetail} from "./MembersData"


const MemberContext = React.createContext ();
//Provider
//Consumer

class MemberProvider extends Component {
	state ={
	members: MembersData,
	MemberDetail: MemberDetail
	};

handleDetail = () =>{
console.log("hello from detail");
};

addMember = () =>{
console.log("hello from add to network");
};
tester = ()=>{
console.log('State members :', this.state.members[0].inNetwork);
console.log('Data members :', MembersData[0].inNetwork);

const tempMembers = [...this.state.members];
tempMembers[0].inNetwork = true
this.setState(()=>{
return{members:tempMembers}
},()=>{
console.log('State members :', this.state.members[0].inNetwork);
console.log('Data members :', MembersData[0].inNetwork);

})
}

	render() {

    return (


      <MemberContext.Provider value={{
...this.state,
     handleDetail:this.handleDetail,
      addMember: this.addMember
      }}
      ><button onClick={this.tester}>test me

      </button>

      	{this.props.children}
      </MemberContext.Provider>
    );


  }
  }

const MemberConsumer = MemberContext.Consumer;

export {MemberProvider, MemberConsumer};
